// frontend/src/App.js

import React, { useState } from 'react';
import ConfigurationForm from './components/ConfigurationForm';
import PouleSchedule from './components/PouleSchedule';
import MatchResults from './components/MatchResults';
import LiveStandings from './components/LiveStandings';
import KnockoutRounds from './components/KnockoutRounds';
import './styles/index.css';

const App = () => {
  const [config, setConfig] = useState(null);
  const [schedule, setSchedule] = useState([]);
  const [results, setResults] = useState({});
  const [standings, setStandings] = useState({});
  const [knockoutStage, setKnockoutStage] = useState(false);
  const [knockoutTeams, setKnockoutTeams] = useState([]);

  const handleConfigSubmit = (newConfig) => {
    setConfig(newConfig);
    setKnockoutStage(false);
    setSchedule([]);
    setResults([]);
    setStandings({});
  };

  const generatePouleSchedule = () => {
    const { teamNames, numPoules, numCourts, matchTime, startTime } = config;
    const poules = Array.from({ length: numPoules }, () => []);

    teamNames.forEach((team, index) => {
      const pouleIndex = index % numPoules;
      poules[pouleIndex].push(team);
    });

    const addMinutes = (time, minutes) => {
      const [hour, min] = time.split(':').map(Number);
      const newMinutes = hour * 60 + min + minutes;
      const newHour = Math.floor(newMinutes / 60) % 24;
      const newMin = newMinutes % 60;
      return `${String(newHour).padStart(2, '0')}:${String(newMin).padStart(2, '0')}`;
    };

    let currentTime = startTime;
    const generatedSchedule = [];
    poules.forEach((poule, pouleIndex) => {
      for (let i = 0; i < poule.length - 1; i++) {
        for (let j = i + 1; j < poule.length; j++) {
          const nonPlayingTeams = teamNames.filter(
            (team) => team !== poule[i] && team !== poule[j]
          );
          const referee = nonPlayingTeams[(generatedSchedule.length) % nonPlayingTeams.length];

          generatedSchedule.push({
            time: currentTime,
            teams: `${poule[i]} vs ${poule[j]}`,
            poule: pouleIndex + 1,
            court: (generatedSchedule.length % numCourts) + 1,
            referee,
            score1: 0,
            score2: 0,
          });

          currentTime = addMinutes(currentTime, matchTime);
        }
      }
    });
    setSchedule(generatedSchedule);
  };

const updateStandings = (newResults, currentStandings) => {
  const updatedStandings = { ...currentStandings }; // Start with the current standings

  newResults.forEach((match) => {
    const { teams, score1, score2, poule } = match;

    console.log(`Updating standings for Poule ${poule}:`, match); // Debug log

    // Initialize the poule in standings if not present
    if (!updatedStandings[poule]) {
      updatedStandings[poule] = {};
    }

    // Parse team names and scores
    const [team1, team2] = teams.split(' vs ').map(t => t.trim());
    const team1Score = parseInt(score1, 10);
    const team2Score = parseInt(score2, 10);

    // Initialize each team in the poule standings if not already there
    [team1, team2].forEach(team => {
      if (!updatedStandings[poule][team]) {
        updatedStandings[poule][team] = {
          name: team,
          played: 0,
          won: 0,
          points: 0,
          scored: 0,
          against: 0
        };
      }
    });

    // Update match statistics for both teams
    const team1Stats = updatedStandings[poule][team1];
    const team2Stats = updatedStandings[poule][team2];

    team1Stats.played++;
    team2Stats.played++;
    team1Stats.scored += team1Score;
    team1Stats.against += team2Score;
    team2Stats.scored += team2Score;
    team2Stats.against += team1Score;

    // Assign points based on match outcome
    if (team1Score > team2Score) {
      team1Stats.won++;
      team1Stats.points += 3;
    } else if (team2Score > team1Score) {
      team2Stats.won++;
      team2Stats.points += 3;
    } else {
      // If draw, each team gets 1 point
      team1Stats.points += 1;
      team2Stats.points += 1;
    }
  });

  console.log("Final updated standings:", JSON.stringify(updatedStandings, null, 2)); // Final debug log
  return updatedStandings;
};


  const debugStandings = (results) => {
    console.log('Debug: Processing Results');
    results.forEach(match => {
      console.log(`Match: ${match.teams}`);
      console.log(`Scores: ${match.score1}-${match.score2}`);
      console.log(`Poule: ${match.poule}`);
    });

    const standings = updateStandings(results);
    console.log('Debug: Final Standings', standings);
    return standings;
  };

const handleSaveResults = (newResults) => {
  if (!Array.isArray(newResults)) {
    console.error("Expected newResults to be an array");
    return;
  }

  const completeResults = newResults.filter(result =>
    result.teams &&
    result.score1 !== undefined &&
    result.score2 !== undefined &&
    result.poule
  );

  if (completeResults.length > 0) {
    console.log("Saving results:", completeResults);
    const newStandings = updateStandings(completeResults);
    console.log("New standings after update:", newStandings);
    setResults(completeResults);
    setStandings(newStandings);
  } else {
    console.warn("No complete match results to save");
  }
};

  return (
    <div>
      <h1>Volleyball Tournament Configuration</h1>
      {!config ? (
        <ConfigurationForm onSubmit={handleConfigSubmit} />
      ) : (
        <>
          <button onClick={generatePouleSchedule}>Generate Schedule</button>
          <PouleSchedule schedule={schedule} />
          <MatchResults matches={schedule} onSaveResults={handleSaveResults} />
          <LiveStandings standings={standings} />
          <button onClick={() => setKnockoutStage(true)}>Save Results & Start Knockout Rounds</button>
          {knockoutStage && <KnockoutRounds teams={knockoutTeams} />}
        </>
      )}
    </div>
  );
};

export default App;
