// frontend/src/components/MatchResults.js
import React, { useState } from 'react';
import '../styles/MatchResults.css';

const MatchResults = ({ matches, onSaveResults }) => {
  const [localResults, setLocalResults] = useState([]);

  useEffect(() => {
    setLocalResults(matches.map(match => ({ ...match, score1: 0, score2: 0 })));
  }, [matches]);

  const handleScoreChange = (index, field, value) => {
    const updatedResults = [...localResults];
    updatedResults[index][field] = parseInt(value) || 0;
    setLocalResults(updatedResults);
  };

  const handleSave = () => {
    onSaveResults(localResults);
  };

  return (
    <div>
      <h3>Enter Match Results</h3>
      {localResults.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Time</th>
              <th>Match</th>
              <th>Court</th>
              <th>Referee</th>
              <th>Score 1</th>
              <th>Score 2</th>
            </tr>
          </thead>
          <tbody>
            {localResults.map((match, index) => (
              <tr key={index}>
                <td>{match.time}</td>
                <td>{match.teams}</td>
                <td>{match.court}</td>
                <td>{match.referee}</td>
                <td>
                  <input
                    type="number"
                    value={match.score1}
                    onChange={(e) => handleScoreChange(index, 'score1', e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={match.score2}
                    onChange={(e) => handleScoreChange(index, 'score2', e.target.value)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No matches scheduled.</p>
      )}
      <button onClick={handleSave}>Save Results</button>
    </div>
  );
};

export default MatchResults;
