// frontend/src/App.js
import React, { useState } from 'react';
import ConfigurationForm from './components/ConfigurationForm';
import PouleSchedule from './components/PouleSchedule';
import MatchResults from './components/MatchResults';
import LiveStandings from './components/LiveStandings';
import './styles/index.css';

const App = () => {
  const [config, setConfig] = useState(null);
  const [schedule, setSchedule] = useState([]);
  const [results, setResults] = useState([]);
  const [standings, setStandings] = useState([]);

  const addMinutes = (time, minutes) => {
    const [hour, min] = time.split(':').map(Number);
    const newMinutes = hour * 60 + min + minutes;
    const newHour = Math.floor(newMinutes / 60) % 24;
    const newMin = newMinutes % 60;
    return `${String(newHour).padStart(2, '0')}:${String(newMin).padStart(2, '0')}`;
  };

  const handleConfigSubmit = (newConfig) => {
    setConfig(newConfig);
    generatePouleSchedule(newConfig);
  };

  const generatePouleSchedule = (config) => {
    const { numPoules, teamNames, startTime, matchTime, numCourts } = config;
    const poules = Array.from({ length: numPoules }, () => []);

    teamNames.forEach((team, index) => {
      const pouleIndex = index % numPoules;
      poules[pouleIndex].push(team);
    });

    let currentTime = startTime;
    const newSchedule = [];
    poules.forEach((poule, pouleIndex) => {
      for (let i = 0; i < poule.length - 1; i++) {
        for (let j = i + 1; j < poule.length; j++) {
          newSchedule.push({
            time: currentTime,
            teams: `${poule[i]} vs ${poule[j]}`,
            court: (newSchedule.length % numCourts) + 1,
            referee: poule[(j + 1) % poule.length],
            poule: pouleIndex + 1
          });
          currentTime = addMinutes(currentTime, matchTime);
        }
      }
    });
    setSchedule(newSchedule);
  };

  const handleSaveResults = (updatedResults) => {
    setResults(updatedResults);
    updateStandings(updatedResults);
  };

  const calculateStandings = (results) => {
    const standings = {};
    results.forEach(({ teams, score1, score2 }) => {
      const [team1, team2] = teams.split(' vs ');
      if (!standings[team1]) standings[team1] = { name: team1, played: 0, won: 0, points: 0, scored: 0, against: 0 };
      if (!standings[team2]) standings[team2] = { name: team2, played: 0, won: 0, points: 0, scored: 0, against: 0 };

      standings[team1].played += 1;
      standings[team2].played += 1;
      standings[team1].scored += parseInt(score1, 10);
      standings[team1].against += parseInt(score2, 10);
      standings[team2].scored += parseInt(score2, 10);
      standings[team2].against += parseInt(score1, 10);

      if (score1 > score2) {
        standings[team1].won += 1;
        standings[team1].points += 3;
      } else if (score2 > score1) {
        standings[team2].won += 1;
        standings[team2].points += 3;
      } else {
        standings[team1].points += 1;
        standings[team2].points += 1;
      }
    });

    return Object.values(standings)
      .sort((a, b) => b.points - a.points || b.scored - a.scored || a.against - b.against);
  };

  const updateStandings = (results) => {
    const updatedStandings = calculateStandings(results);
    setStandings(updatedStandings);
  };

  return (
    <div>
      <h1>Volleyball Tournament Configuration</h1>
      {!config ? (
        <ConfigurationForm onSubmit={handleConfigSubmit} />
      ) : (
        <>
          <PouleSchedule schedule={schedule} />
          <MatchResults schedule={schedule} onSaveResults={handleSaveResults} />
          <LiveStandings standings={standings} />
        </>
      )}
    </div>
  );
};

export default App;
