// frontend/src/components/ConfigurationForm.js
import React, { useState } from 'react';
import axios from 'axios';
import TournamentSchedule from './TournamentSchedule';
import '../styles/ConfigurationForm.css';

function ConfigurationForm() {
    const [numTeams, setNumTeams] = useState(6);
    const [numPoules, setNumPoules] = useState(2);
    const [numCourts, setNumCourts] = useState(2);
    const [matchTime, setMatchTime] = useState(40);
    const [startTime, setStartTime] = useState('09:00');
    const [tournamentDuration, setTournamentDuration] = useState(8);
    const [error, setError] = useState(null);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [tournamentId, setTournamentId] = useState(null);
    const [success, setSuccess] = useState(null);

    const handleSubmit = async () => {
        setError(null);
        setIsSubmitting(true);

        const config = {
            numTeams: parseInt(numTeams, 10),
            numPoules: parseInt(numPoules, 10),
            numCourts: parseInt(numCourts, 10),
            matchTime: parseInt(matchTime, 10),
            startTime,
            tournamentDuration: parseInt(tournamentDuration, 10),
        };

        console.log('Submitting tournament configuration:', config);

        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/tournament`, config, {
                headers: {
                    'Content-Type': 'application/json',
                }
            });

            console.log('Tournament created:', response.data);
            setTournamentId(response.data.id);
            setSuccess('Toernooi succesvol aangemaakt!');
        } catch (error) {
            console.error('Error creating tournament:', error);
            setError(
                error.response?.data?.message ||
                'Er is een fout opgetreden bij het maken van het schema.'
            );
        } finally {
            setIsSubmitting(false);
        }
    };

    const generateSchedule = async (tournamentId) => {
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/tournament/${tournamentId}/schedule`);
            console.log('Schedule created:', response.data);
            setSuccess('Wedstrijdschema is aangemaakt!');
        } catch (error) {
            console.error('Error creating schedule:', error);
            setError('Er is een fout opgetreden bij het maken van het schema.');
        }
    };

    const handleNumberInput = (setter) => (e) => {
        const value = e.target.value;
        if (value === '' || /^\d+$/.test(value)) {
            setter(value);
        }
    };

    return (
        <div className="configuration">
            <h2>Configuratie</h2>
            {error && <div className="error-message" style={{ color: 'red', marginBottom: '1rem' }}>{error}</div>}
            {success && <div className="success-message" style={{ color: 'green', marginBottom: '1rem' }}>{success}</div>}

            <div className="form-group">
                <label>Aantal Teams:</label>
                <input type="number" value={numTeams} onChange={handleNumberInput(setNumTeams)} min="2" required />
            </div>

            <div className="form-group">
                <label>Aantal Poules:</label>
                <input type="number" value={numPoules} onChange={handleNumberInput(setNumPoules)} min="1" required />
            </div>

            <div className="form-group">
                <label>Aantal Velden:</label>
                <input type="number" value={numCourts} onChange={handleNumberInput(setNumCourts)} min="1" required />
            </div>

            <div className="form-group">
                <label>Tijd per Wedstrijd (minuten):</label>
                <input type="number" value={matchTime} onChange={handleNumberInput(setMatchTime)} min="1" required />
            </div>

            <div className="form-group">
                <label>Start Tijd (HH:MM):</label>
                <input type="time" value={startTime} onChange={(e) => setStartTime(e.target.value)} required />
            </div>

            <div className="form-group">
                <label>Toernooi Duur (uren):</label>
                <input type="number" value={tournamentDuration} onChange={handleNumberInput(setTournamentDuration)} min="1" required />
            </div>

            <button onClick={handleSubmit} disabled={isSubmitting} style={{ marginTop: '1rem' }}>
                {isSubmitting ? 'Bezig met maken...' : 'Maak Toernooi'}
            </button>

            {tournamentId && (
                <button onClick={() => generateSchedule(tournamentId)} style={{ marginTop: '1rem', marginLeft: '1rem' }}>
                    Genereer Wedstrijdschema
                </button>
            )}

            {tournamentId && <TournamentSchedule tournamentId={tournamentId} />}
        </div>
    );
}

export default ConfigurationForm;
