// frontend/src/components/MatchResults.js
import React, { useState } from 'react';
import '../styles/MatchResults.css';

const MatchResults = ({ schedule, onSaveResults }) => {
  // Create a state to hold scores for each match
  const [scores, setScores] = useState(
    schedule.map((match) => ({ ...match, score1: '', score2: '' }))
  );

  // Handle score input change
  const handleScoreChange = (index, field, value) => {
    const updatedScores = [...scores];
    updatedScores[index][field] = value;
    setScores(updatedScores);
  };

  // Handle save results button click
  const handleSave = () => {
    // Filter out matches with both scores entered
    const completedResults = scores
      .filter(match => match.score1 !== '' && match.score2 !== '')
      .map(match => ({
        teams: match.teams,
        score1: parseInt(match.score1, 10),
        score2: parseInt(match.score2, 10),
      }));

    onSaveResults(completedResults);
  };

  return (
    <div>
      <h3>Enter Match Results</h3>
      <table>
        <thead>
          <tr>
            <th>Time</th>
            <th>Match</th>
            <th>Court</th>
            <th>Referee</th>
            <th>Score 1</th>
            <th>Score 2</th>
          </tr>
        </thead>
        <tbody>
          {scores.map((match, index) => (
            <tr key={index}>
              <td>{match.time}</td>
              <td>{match.teams}</td>
              <td>{match.court}</td>
              <td>{match.referee}</td>
              <td>
                <input
                  type="number"
                  value={match.score1}
                  onChange={(e) =>
                    handleScoreChange(index, 'score1', e.target.value)
                  }
                />
              </td>
              <td>
                <input
                  type="number"
                  value={match.score2}
                  onChange={(e) =>
                    handleScoreChange(index, 'score2', e.target.value)
                  }
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleSave}>Save Results</button>
    </div>
  );
};

export default MatchResults;
