// frontend/src/components/LiveStandings.js
import React from 'react';

function LiveStandings({ standings }) {
  // Group standings by poule (assuming each team belongs to a poule)
  const poules = standings.reduce((acc, team) => {
    const pouleKey = `Poule ${team.poule}`;
    if (!acc[pouleKey]) {
      acc[pouleKey] = [];
    }
    acc[pouleKey].push(team);
    return acc;
  }, {});

  return (
    <div className="live-standings">
      <h2>Live Tournament Standings</h2>
      {Object.keys(poules).map((pouleKey, index) => (
        <div key={index} className="poule">
          <h3>{pouleKey}</h3>
          <table>
            <thead>
              <tr>
                <th>Team</th>
                <th>Played</th>
                <th>Won</th>
                <th>Points</th>
              </tr>
            </thead>
            <tbody>
              {poules[pouleKey].map((team, idx) => (
                <tr key={idx}>
                  <td>{team.name}</td>
                  <td>{team.played}</td>
                  <td>{team.won}</td>
                  <td>{team.points}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}

export default LiveStandings;
