import React, { useState, useEffect } from 'react';
import ConfigurationForm from './components/ConfigurationForm';
import PouleSchedule from './components/PouleSchedule';
import MatchResults from './components/MatchResults';
import Standings from './components/Standings';
import KnockoutRounds from './components/KnockoutRounds';
import axios from 'axios';

function App() {
  const [tournamentId, setTournamentId] = useState(null);
  const [tournamentConfig, setTournamentConfig] = useState(null);
  const [schedule, setSchedule] = useState([]);
  const [matches, setMatches] = useState([]);

  const handleCreateSchedule = async (config) => {
    setTournamentConfig(config);
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/tournament`, config);
      if (response.status === 201) {
        setTournamentId(response.data.id);
        console.log("Tournament created successfully:", response.data);
      }
    } catch (error) {
      console.error("Error creating tournament:", error);
    }
  };

  useEffect(() => {
    const fetchSchedule = async () => {
      if (tournamentId) {
        try {
          const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/tournament/${tournamentId}/schedule`);
          if (response.status === 200) {
            setSchedule(response.data);
            setMatches(response.data); // Assuming schedule and matches are related
          }
        } catch (error) {
          console.error("Error fetching schedule:", error);
        }
      }
    };
    fetchSchedule();
  }, [tournamentId]);

  const updateMatchScore = (matchId, scores) => {
    setMatches((prevMatches) =>
      prevMatches.map((match) =>
        match.id === matchId ? { ...match, ...scores, completed: true } : match
      )
    );
  };

  return (
    <div className="App">
      <h1>Volleybal Toernooi Web App</h1>
      <ConfigurationForm onSubmit={handleCreateSchedule} />
      {tournamentId && (
        <>
          <PouleSchedule schedule={schedule} />
          <MatchResults matches={matches} updateScore={updateMatchScore} />
          <Standings matches={matches} />
          <KnockoutRounds matches={matches} />
        </>
      )}
    </div>
  );
}

export default App;
