// frontend/src/components/PouleSchedule.js
import React from 'react';
import '../styles/PouleSchedule.css';  // Import the CSS file

function PouleSchedule({ schedule }) {
  // Group matches by poule
  const poules = schedule.reduce((acc, match) => {
    const pouleKey = `Poule ${match.poule}`;
    if (!acc[pouleKey]) {
      acc[pouleKey] = [];
    }
    acc[pouleKey].push(match);
    return acc;
  }, {});

  return (
    <div className="poule-schedule">
      <h2>Poule Schedule</h2>
      {Object.keys(poules).map((pouleKey, index) => (
        <div key={index} className="poule">
          <h3>{pouleKey}</h3>
          <table>
            <thead>
              <tr>
                <th>Time</th>
                <th>Match</th>
                <th>Poule</th>
                <th>Court</th>
              </tr>
            </thead>
            <tbody>
              {poules[pouleKey].map((match, idx) => (
                <tr key={idx}>
                  <td>{match.time}</td>
                  <td>{match.teams}</td>
                  <td>{match.poule}</td>
                  <td>{match.court}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}

export default PouleSchedule;
