// frontend/src/components/MatchResults.js
import React from 'react';
import '../styles/MatchResults.css';

function MatchResults({ schedule, onSaveResults }) {
  const [localResults, setLocalResults] = useState([]);

  const handleScoreChange = (index, teamKey, value) => {
    const updatedResults = [...localResults];
    if (!updatedResults[index]) {
      updatedResults[index] = { teams: schedule[index].teams, score1: 0, score2: 0 };
    }
    updatedResults[index][teamKey] = parseInt(value, 10) || 0;
    setLocalResults(updatedResults);
  };

  const handleSave = () => {
    onSaveResults(localResults);
  };

  return (
    <div>
      <h3>Enter Match Results</h3>
      <table>
        <thead>
          <tr>
            <th>Time</th>
            <th>Match</th>
            <th>Court</th>
            <th>Referee</th>
            <th>Score 1</th>
            <th>Score 2</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((match, index) => (
            <tr key={index}>
              <td>{match.time}</td>
              <td>{match.teams}</td>
              <td>{match.court}</td>
              <td>{match.referee}</td>
              <td>
                <input
                  type="number"
                  value={localResults[index]?.score1 || ''}
                  onChange={(e) => handleScoreChange(index, 'score1', e.target.value)}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={localResults[index]?.score2 || ''}
                  onChange={(e) => handleScoreChange(index, 'score2', e.target.value)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleSave}>Save Results</button>
    </div>
  );
}

export default MatchResults;
