import axios from 'axios';
import React, { useState } from 'react';

function ConfigurationForm() {
  const [numTeams, setNumTeams] = useState(6);
  const [numPoules, setNumPoules] = useState(2);
  const [numCourts, setNumCourts] = useState(2);
  const [matchTime, setMatchTime] = useState(40);
  const [startTime, setStartTime] = useState('09:00');
  const [tournamentDuration, setTournamentDuration] = useState(8);

  const handleSubmit = async () => {
  const config = { numTeams, numPoules, numCourts, matchTime, startTime, tournamentDuration };

  try {
    const response = await fetch('http://localhost:5000/api/tournament', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(config),
    });
    if (response.ok) {
      console.log('Tournament configuration saved successfully');
    } else {
      console.error('Failed to save configuration');
    }
  } catch (error) {
    console.error('Error:', error);
  }
};


  return (
    <div className="configuration">
      <h2>Configuratie</h2>
      <label>Aantal Teams:</label>
      <input type="number" value={numTeams} onChange={(e) => setNumTeams(e.target.value)} /><br />
      <label>Aantal Poules:</label>
      <input type="number" value={numPoules} onChange={(e) => setNumPoules(e.target.value)} /><br />
      <label>Aantal Velden:</label>
      <input type="number" value={numCourts} onChange={(e) => setNumCourts(e.target.value)} /><br />
      <label>Tijd per Wedstrijd (minuten):</label>
      <input type="number" value={matchTime} onChange={(e) => setMatchTime(e.target.value)} /><br />
      <label>Start Tijd (HH:MM):</label>
      <input type="time" value={startTime} onChange={(e) => setStartTime(e.target.value)} /><br />
      <label>Toernooi Duur (uren):</label>
      <input type="number" value={tournamentDuration} onChange={(e) => setTournamentDuration(e.target.value)} /><br /><br />
      <button onClick={handleSubmit}>Maak Schema</button>
    </div>
  );
}

export default ConfigurationForm;
