// PouleSchedule.js
import React from 'react';
import '../styles/PouleSchedule.css';

function PouleSchedule({ schedule }) {
  // Group matches by poule for easier visualization
  const matchesByPoule = schedule.reduce((acc, match) => {
    if (!acc[match.poule]) {
      acc[match.poule] = [];
    }
    acc[match.poule].push(match);
    return acc;
  }, {});

  return (
    <div>
      <h2>Wedstrijdschema per Poule</h2>
      {Object.entries(matchesByPoule).map(([poule, matches]) => (
        <div key={poule}>
          <h3>Poule {poule}</h3>
          <table>
            <thead>
              <tr>
                <th>Tijd</th>
                <th>Wedstrijd</th>
                <th>Veld</th>
                <th>Scheidsrechter & Scorekeeper</th>
              </tr>
            </thead>
            <tbody>
              {matches.map((match, index) => (
                <tr key={index}>
                  <td>{match.start_time}</td>
                  <td>Team {match.team1_id} vs Team {match.team2_id}</td>
                  <td>Veld {match.court}</td>
                  <td>{`Referee: Team ${match.refereeTeam}`}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}

export default PouleSchedule;
