// frontend/src/components/LiveStandings.js
import React from 'react';

function LiveStandings({ standings }) {
  return (
    <div>
      <h2>Live Tournament Standings</h2>
      {Object.keys(standings).map((poule) => (
        <div key={poule}>
          <h3>Poule {poule}</h3>
          <table>
            <thead>
              <tr>
                <th>Team</th>
                <th>Played</th>
                <th>Won</th>
                <th>Points</th>
                <th>Scored</th>
                <th>Against</th>
              </tr>
            </thead>
            <tbody>
              {standings[poule].map((team) => (
                <tr key={team.name}>
                  <td>{team.name}</td>
                  <td>{team.played}</td>
                  <td>{team.won}</td>
                  <td>{team.points}</td>
                  <td>{team.scored}</td>
                  <td>{team.against}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}

export default LiveStandings;
