// frontend/src/components/ConfigurationForm.js
import React, { useState } from 'react';
import Button from './ui/button';
import Input from './ui/input';

function ConfigurationForm({ onSubmit }) {
  const [config, setConfig] = useState({
    numTeams: '',
    numPoules: '',
    numCourts: '',
    matchTime: '',
    startTime: '',
    tournamentDuration: '',
  });

  const handleChange = (e) => {
    setConfig({
      ...config,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(config);
  };

  return (
    <form onSubmit={handleSubmit}>
      <Input
        name="numTeams"
        value={config.numTeams}
        onChange={handleChange}
        placeholder="Number of Teams"
        required
      />
      <Input
        name="numPoules"
        value={config.numPoules}
        onChange={handleChange}
        placeholder="Number of Poules"
        required
      />
      <Input
        name="numCourts"
        value={config.numCourts}
        onChange={handleChange}
        placeholder="Number of Courts"
        required
      />
      <Input
        name="matchTime"
        value={config.matchTime}
        onChange={handleChange}
        placeholder="Match Time"
        required
      />
      <Input
        name="startTime"
        value={config.startTime}
        onChange={handleChange}
        placeholder="Start Time"
        required
      />
      <Input
        name="tournamentDuration"
        value={config.tournamentDuration}
        onChange={handleChange}
        placeholder="Tournament Duration"
        required
      />
      <Button type="submit">Create Tournament</Button>
    </form>
  );
}

export default ConfigurationForm;
