import React from 'react';
import '../styles/PouleSchedule.css';

function PouleSchedule({ schedule }) {
  // Render the schedule of matches
  return (
    <div>
      <h2>Wedstrijdschema</h2>
      <table>
        <thead>
          <tr>
            <th>Tijd</th>
            <th>Wedstrijd</th>
            <th>Poule</th>
            <th>Veld</th>
            <th>Scheidsrechter & Scorekeeper</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((match, index) => (
            <tr key={index}>
              <td>{match.time}</td>
              <td>{match.teamA} vs {match.teamB}</td>
              <td>{match.poule}</td>
              <td>Veld {match.court}</td>
              <td>{match.refereeTeam}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PouleSchedule;
