// frontend/src/components/MatchResults.js
import React, { useState } from 'react';
import '../styles/MatchResults.css';

function MatchResults({ matches = [], onSaveResults }) {
    const [results, setResults] = useState(
        matches.map(match => ({
            ...match,
            score1: match.score1 || 0,
            score2: match.score2 || 0,
        }))
    );

    const handleScoreChange = (index, scoreKey, value) => {
        const updatedResults = [...results];
        updatedResults[index][scoreKey] = parseInt(value) || 0;
        setResults(updatedResults);
    };

    const handleSave = () => {
        onSaveResults(results);
    };

    return (
        <div>
            <h3>Enter Match Results</h3>
            <table>
                <thead>
                    <tr>
                        <th>Time</th>
                        <th>Match</th>
                        <th>Court</th>
                        <th>Referee</th>
                        <th>Score 1</th>
                        <th>Score 2</th>
                    </tr>
                </thead>
                <tbody>
                    {results.map((match, index) => (
                        <tr key={index}>
                            <td>{match.time}</td>
                            <td>{match.teams}</td>
                            <td>{match.court}</td>
                            <td>{match.referee}</td>
                            <td>
                                <input
                                    type="number"
                                    value={match.score1}
                                    onChange={(e) =>
                                        handleScoreChange(index, 'score1', e.target.value)
                                    }
                                />
                            </td>
                            <td>
                                <input
                                    type="number"
                                    value={match.score2}
                                    onChange={(e) =>
                                        handleScoreChange(index, 'score2', e.target.value)
                                    }
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={handleSave}>Save Results</button>
        </div>
    );
}

export default MatchResults;
