// frontend/src/components/MatchResults.js
import React, { useState, useEffect } from 'react';
import '../styles/MatchResults.css';  // Import the CSS file

function MatchResults({ matches, onSaveResults }) {
  const [results, setResults] = useState(
    matches.map((match) => ({
      teams: match.teams,
      score1: '',
      score2: '',
    }))
  );

  const handleChange = (index, field, value) => {
    const updatedResults = [...results];
    updatedResults[index][field] = value;
    setResults(updatedResults);
  };

  const handleSave = () => {
    const parsedResults = results.map((result) => ({
      ...result,
      score1: parseInt(result.score1, 10) || 0,
      score2: parseInt(result.score2, 10) || 0,
    }));
    onSaveResults(parsedResults);
  };

  return (
    <div>
      <h2>Enter Match Results</h2>
      <table>
        <thead>
          <tr>
            <th>Match</th>
            <th>Score 1</th>
            <th>Score 2</th>
          </tr>
        </thead>
        <tbody>
          {results.map((result, index) => (
            <tr key={index}>
              <td>{result.teams}</td>
              <td>
                <input
                  type="number"
                  value={result.score1}
                  onChange={(e) => handleChange(index, 'score1', e.target.value)}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={result.score2}
                  onChange={(e) => handleChange(index, 'score2', e.target.value)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleSave}>Save Results</button>
    </div>
  );
}

export default MatchResults;
