import React, { useState } from 'react';
import axios from 'axios';
import '../styles/MatchResults.css';

function MatchResults({ matches, updateScore }) {
  const [scores, setScores] = useState({});

  const handleChange = (matchId, team, value) => {
    setScores((prevScores) => ({
      ...prevScores,
      [matchId]: {
        ...prevScores[matchId],
        [team]: value,
      },
    }));
  };

  const handleSubmit = async (matchId) => {
    const scoresToSubmit = scores[matchId];
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/api/tournament/match/${matchId}/score`, scoresToSubmit);
      updateScore(matchId, scoresToSubmit);
    } catch (error) {
      console.error('Error updating score:', error);
    }
  };

  return (
    <div>
      <h2>Wedstrijdresultaten</h2>
      {matches.length > 0 ? (
        matches.map((match) => (
          <div key={match.id} className="match-result">
            <p>{match.teamA} vs {match.teamB}</p>
            <input
              type="number"
              value={scores[match.id]?.teamA || ''}
              onChange={(e) => handleChange(match.id, 'teamA', e.target.value)}
              placeholder={`Score ${match.teamA}`}
            />
            -
            <input
              type="number"
              value={scores[match.id]?.teamB || ''}
              onChange={(e) => handleChange(match.id, 'teamB', e.target.value)}
              placeholder={`Score ${match.teamB}`}
            />
            <button onClick={() => handleSubmit(match.id)}>Submit Score</button>
          </div>
        ))
      ) : (
        <p>Geen wedstrijden om in te voeren.</p>
      )}
    </div>
  );
}

export default MatchResults;
