// frontend/src/components/ConfigurationForm.js
import React, { useState, useEffect } from 'react';
import Button from './ui/button';
import Input from './ui/input';
import '../styles/ConfigurationForm.css';

function ConfigurationForm({ onSubmit }) {
  const [numTeams, setNumTeams] = useState(48);
  const [numPoules, setNumPoules] = useState(8);
  const [numCourts, setNumCourts] = useState(12);
  const [matchTime, setMatchTime] = useState(15); // in minutes
  const [startTime, setStartTime] = useState("0900");
  const [tournamentDuration, setTournamentDuration] = useState(480); // 8 hours by default

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      numTeams,
      numPoules,
      numCourts,
      matchTime,
      startTime,
      tournamentDuration,
      teamNames: Array.from({ length: numTeams }, (_, i) => `Team ${i + 1}`)
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Configuration</h2>
      <label>
        Number of Teams:
        <input type="number" value={numTeams} onChange={(e) => setNumTeams(Number(e.target.value))} />
      </label>
      <label>
        Number of Poules:
        <input type="number" value={numPoules} onChange={(e) => setNumPoules(Number(e.target.value))} />
      </label>
      <label>
        Number of Courts:
        <input type="number" value={numCourts} onChange={(e) => setNumCourts(Number(e.target.value))} />
      </label>
      <label>
        Match Time (mins):
        <input type="number" value={matchTime} onChange={(e) => setMatchTime(Number(e.target.value))} />
      </label>
      <label>
        Start Time:
        <input type="text" value={startTime} onChange={(e) => setStartTime(e.target.value)} placeholder="0900" />
      </label>
      <label>
        Tournament Duration (mins):
        <input type="number" value={tournamentDuration} onChange={(e) => setTournamentDuration(Number(e.target.value))} />
      </label>
      <button type="submit">Create Tournament</button>
    </form>
  );
}

export default ConfigurationForm;
