import React from 'react';
import { Table } from '../ui/table';


function PouleSchedule({ schedule = [] }) {
  if (!Array.isArray(schedule) || schedule.length === 0) {
    return (
      <div className="p-4">
        <h2 className="text-2xl font-bold mb-4">Wedstrijdschema per Poule</h2>
        <p className="text-gray-500">Geen wedstrijdschema beschikbaar.</p>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Wedstrijdschema per Poule</h2>
      {schedule.map((poule, pouleIndex) => (
        <div key={pouleIndex} className="mb-8">
          <h3 className="text-xl font-semibold mb-4">Poule {poule.pouleNumber}</h3>
          <div className="border rounded-lg overflow-hidden">
            <Table>
              <Table.Header>
                <Table.Row>
                  <Table.Head>Tijd</Table.Head>
                  <Table.Head>Wedstrijd</Table.Head>
                  <Table.Head>Veld</Table.Head>
                  <Table.Head>Scheidsrechter</Table.Head>
                </Table.Row>
              </Table.Header>
              <Table.Body>
                {Array.isArray(poule.matches) && poule.matches.map((match, matchIndex) => (
                  <Table.Row key={matchIndex}>
                    <Table.Cell>{match.time}</Table.Cell>
                    <Table.Cell>
                      {match.teamA} vs {match.teamB}
                      {(match.scoreA !== null && match.scoreB !== null) && 
                        <span className="ml-2 text-sm text-gray-500">
                          ({match.scoreA} - {match.scoreB})
                        </span>
                      }
                    </Table.Cell>
                    <Table.Cell>Veld {match.court}</Table.Cell>
                    <Table.Cell>{match.refereeTeam}</Table.Cell>
                  </Table.Row>
                ))}
              </Table.Body>
            </Table>
          </div>
        </div>
      ))}
    </div>
  );
}

export default PouleSchedule;
