import React, { useState } from 'react';
import axios from 'axios';
import { Button } from './ui/button.js';
import { Input } from './ui/input.js';
import { Card } from './ui/card.js';

function MatchResults({ matches, updateScore }) {
  const [scores, setScores] = useState({});
  const [error, setError] = useState(null);

  const handleChange = (matchId, team, value) => {
    const numValue = parseInt(value, 10);
    if (isNaN(numValue) || numValue < 0) return;

    setScores(prevScores => ({
      ...prevScores,
      [matchId]: {
        ...prevScores[matchId],
        [team]: numValue
      }
    }));
  };

  const handleSubmit = async (matchId) => {
    try {
      setError(null);
      const scoresToSubmit = scores[matchId];

      if (!scoresToSubmit || !scoresToSubmit.teamA || !scoresToSubmit.teamB) {
        setError('Voer alstublieft beide scores in');
        return;
      }

      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/tournament/match/${matchId}/score`, 
        scoresToSubmit
      );

      updateScore(matchId, scoresToSubmit);

      // Clear scores for this match
      setScores(prevScores => {
        const newScores = { ...prevScores };
        delete newScores[matchId];
        return newScores;
      });
    } catch (error) {
      console.error('Error updating score:', error);
      setError('Er is een fout opgetreden bij het opslaan van de score');
    }
  };

  if (!Array.isArray(matches) || matches.length === 0) {
    return (
      <div className="p-4">
        <h2 className="text-2xl font-bold mb-4">Wedstrijdresultaten</h2>
        <p className="text-gray-500">Geen wedstrijden om in te voeren.</p>
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Wedstrijdresultaten</h2>
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {matches.map((match) => (
          <Card key={match.id} className="p-4">
            <div className="flex flex-col gap-4">
              <div className="text-lg font-semibold">
                {match.teamA} vs {match.teamB}
              </div>
              <div className="flex items-center gap-2">
                <Input
                  type="number"
                  min="0"
                  value={scores[match.id]?.teamA || ''}
                  onChange={(e) => handleChange(match.id, 'teamA', e.target.value)}
                  placeholder={`Score ${match.teamA}`}
                  className="w-20"
                />
                <span className="text-lg font-bold">-</span>
                <Input
                  type="number"
                  min="0"
                  value={scores[match.id]?.teamB || ''}
                  onChange={(e) => handleChange(match.id, 'teamB', e.target.value)}
                  placeholder={`Score ${match.teamB}`}
                  className="w-20"
                />
                <Button 
                  onClick={() => handleSubmit(match.id)}
                  disabled={!scores[match.id]?.teamA || !scores[match.id]?.teamB}
                >
                  Opslaan
                </Button>
              </div>
              {match.completed && (
                <div className="text-sm text-green-600">
                  Eindstand: {match.scoreA} - {match.scoreB}
                </div>
              )}
            </div>
          </Card>
        ))}
      </div>
      {error && (
        <div className="mt-4 p-4 bg-red-100 text-red-700 rounded">
          {error}
        </div>
      )}
    </div>
  );
}

export default MatchResults;
