// frontend/src/components/MatchResults.js
import React, { useState, useEffect } from 'react';
import '../styles/MatchResults.css';

const MatchResults = ({ matches, onSaveResults }) => {
  const [localResults, setLocalResults] = useState([]);

  useEffect(() => {
    setLocalResults(matches.map((match) => ({ ...match, score1: '', score2: '' })));
  }, [matches]);

  const handleScoreChange = (index, field, value) => {
    const updatedResults = [...localResults];
    updatedResults[index][field] = value;
    setLocalResults(updatedResults);
  };

  const handleSave = () => {
    onSaveResults(localResults);
  };

  // Group matches by poule for readability
  const groupedMatches = localResults.reduce((acc, match) => {
    if (!acc[match.poule]) acc[match.poule] = [];
    acc[match.poule].push(match);
    return acc;
  }, {});

  return (
    <div>
      <h2>Enter Match Results</h2>
      {Object.keys(groupedMatches).map((poule) => (
        <div key={poule}>
          <h3>Poule {poule}</h3>
          <table>
            <thead>
              <tr>
                <th>Time</th>
                <th>Match</th>
                <th>Court</th>
                <th>Referee</th>
                <th>Score 1</th>
                <th>Score 2</th>
              </tr>
            </thead>
            <tbody>
              {groupedMatches[poule].map((match, index) => (
                <tr key={index}>
                  <td>{match.time}</td>
                  <td>{match.teams}</td>
                  <td>{match.court}</td>
                  <td>{match.referee}</td>
                  <td>
                    <input
                      type="number"
                      value={match.score1}
                      onChange={(e) => handleScoreChange(index, 'score1', e.target.value)}
                      min="0"
                      step="1"
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      value={match.score2}
                      onChange={(e) => handleScoreChange(index, 'score2', e.target.value)}
                      min="0"
                      step="1"
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
      <button onClick={handleSave}>Save Results</button>
    </div>
  );
};

export default MatchResults;
