// frontend/src/components/ConfigurationForm.js
import React, { useState } from 'react';
import Button from './ui/button';
import Input from './ui/input';
import '../styles/ConfigurationForm.css';  // Import the CSS file

function ConfigurationForm({ onSubmit }) {
  const [config, setConfig] = useState({
    numTeams: '',
    numPoules: '',
    numCourts: '',
    matchTime: '',
    startTime: '',
    tournamentDuration: '',
  });

  const handleChange = (e) => {
    setConfig({
      ...config,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(config);
  };

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <div style={inputGroupStyle}>
        <Input
          name="numTeams"
          value={config.numTeams}
          onChange={handleChange}
          placeholder="Number of Teams"
          required
        />
        <Input
          name="numPoules"
          value={config.numPoules}
          onChange={handleChange}
          placeholder="Number of Poules"
          required
        />
        <Input
          name="numCourts"
          value={config.numCourts}
          onChange={handleChange}
          placeholder="Number of Courts"
          required
        />
        <Input
          name="matchTime"
          value={config.matchTime}
          onChange={handleChange}
          placeholder="Match Time"
          required
        />
        <Input
          name="startTime"
          value={config.startTime}
          onChange={handleChange}
          placeholder="Start Time"
          required
        />
        <Input
          name="tournamentDuration"
          value={config.tournamentDuration}
          onChange={handleChange}
          placeholder="Tournament Duration"
          required
        />
      </div>
      <Button type="submit" style={buttonStyle}>Create Tournament</Button>
    </form>
  );
}

// Inline styles for layout
const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  gap: '10px',
};

const inputGroupStyle = {
  display: 'flex',
  flexWrap: 'wrap',
  gap: '10px',
  maxWidth: '400px',
};

const buttonStyle = {
  padding: '10px 20px',
  fontSize: '16px',
};

export default ConfigurationForm;
