// frontend/src/App.js
import React, { useState } from 'react';
import apiClient from './apiClient';
import ConfigurationForm from './components/ConfigurationForm';
import PouleSchedule from './components/PouleSchedule';
import MatchResults from './components/MatchResults';
import LiveStandings from './components/LiveStandings';
import './styles/index.css';

const App = () => {
  const [config, setConfig] = useState(null);
  const [schedule, setSchedule] = useState([]);
  const [results, setResults] = useState([]);
  const [standings, setStandings] = useState([]);

  const generateTeamPoules = (numTeams, numPoules) => {
    const teamPoules = {};
    config.teamNames.forEach((team, index) => {
      teamPoules[team] = (index % numPoules) + 1; // evenly distribute teams into poules
    });
    return teamPoules;
  };

  const generatePouleSchedule = () => {
    const teamPoules = generateTeamPoules(config.numTeams, config.numPoules);
    const poules = Array.from({ length: config.numPoules }, () => []);

    config.teamNames.forEach((team) => {
      const pouleIndex = teamPoules[team] - 1;
      poules[pouleIndex].push(team);
    });

    const addMinutes = (time, minutes) => {
      const hours = parseInt(time.slice(0, 2), 10);
      const mins = parseInt(time.slice(2), 10) + minutes;
      const newHours = hours + Math.floor(mins / 60);
      const newMins = mins % 60;
      return `${String(newHours).padStart(2, '0')}:${String(newMins).padStart(2, '0')}`;
    };

    let currentTime = config.startTime;
    const schedule = [];
    poules.forEach((poule, pouleIndex) => {
      for (let i = 0; i < poule.length - 1; i++) {
        for (let j = i + 1; j < poule.length; j++) {
          schedule.push({
            time: currentTime,
            teams: `${poule[i]} vs ${poule[j]}`,
            poule: pouleIndex + 1,
            court: (schedule.length % config.numCourts) + 1,
          });
          currentTime = addMinutes(currentTime.replace(':', ''), config.matchTime);
        }
      }
    });
    setSchedule(schedule);
    return schedule;
  };

  const calculateStandings = (results) => {
    const teamPoules = generateTeamPoules(config.numTeams, config.numPoules);
    const standings = {};

    results.forEach(({ teams, score1, score2 }) => {
      const [team1, team2] = teams.split(' vs ');
      const poule1 = teamPoules[team1];
      const poule2 = teamPoules[team2];

      if (poule1 === undefined || poule2 === undefined) {
        console.error(`Poule data missing for ${team1} or ${team2}`);
        return;
      }

      if (!standings[team1]) standings[team1] = { name: team1, played: 0, won: 0, points: 0, poule: poule1 };
      if (!standings[team2]) standings[team2] = { name: team2, played: 0, won: 0, points: 0, poule: poule2 };

      standings[team1].played += 1;
      standings[team2].played += 1;

      if (score1 > score2) {
        standings[team1].won += 1;
        standings[team1].points += 3;
      } else if (score2 > score1) {
        standings[team2].won += 1;
        standings[team2].points += 3;
      } else {
        standings[team1].points += 1;
        standings[team2].points += 1;
      }
    });

    setStandings(Object.values(standings).sort((a, b) => b.points - a.points || b.won - a.won));
  };

  const handleSaveResults = (newResults) => {
    setResults(newResults);
    calculateStandings(newResults);
  };

  return (
    <div>
      <h1>Volleyball Tournament Configuration</h1>
      {!config ? (
        <ConfigurationForm onSubmit={setConfig} />
      ) : (
        <>
          <button onClick={generatePouleSchedule}>Generate Schedule</button>
          <PouleSchedule schedule={schedule} />
          <MatchResults matches={schedule} onSaveResults={handleSaveResults} />
        </>
      )}
      <LiveStandings standings={standings} />
    </div>
  );
};

export default App;
