// frontend/src/App.js
import React, { useState } from 'react';
import ConfigurationForm from './components/ConfigurationForm';
import PouleSchedule from './components/PouleSchedule';
import MatchResults from './components/MatchResults';
import LiveStandings from './components/LiveStandings';
import './styles/index.css';

const App = () => {
  const [config, setConfig] = useState(null);
  const [schedule, setSchedule] = useState([]);
  const [results, setResults] = useState([]);
  const [standings, setStandings] = useState({});
  const [teamNames, setTeamNames] = useState([]);

  const handleConfigSubmit = (newConfig) => {
    setConfig(newConfig);
    setTeamNames(newConfig.teamNames);
  };

  const generatePouleSchedule = () => {
    const poules = Array.from({ length: config.numPoules }, () => []);
    teamNames.forEach((team, index) => {
      poules[index % config.numPoules].push(team);
    });

    let currentTime = config.startTime;
    const schedule = [];

    poules.forEach((poule, pouleIndex) => {
      for (let i = 0; i < poule.length - 1; i++) {
        for (let j = i + 1; j < poule.length; j++) {
          schedule.push({
            time: currentTime,
            teams: `${poule[i]} vs ${poule[j]}`,
            poule: pouleIndex + 1,
            court: (schedule.length % config.numCourts) + 1,
            referee: teamNames.find(team => team !== poule[i] && team !== poule[j]),
          });
          currentTime = addMinutes(currentTime, config.matchTime);
        }
      }
    });
    setSchedule(schedule);
  };

  const addMinutes = (time, minutes) => {
    const [hour, min] = time.split(':').map(Number);
    const newMinutes = hour * 60 + min + minutes;
    return `${String(Math.floor(newMinutes / 60) % 24).padStart(2, '0')}:${String(newMinutes % 60).padStart(2, '0')}`;
  };

  const handleSaveResults = (updatedResults) => {
    setResults(updatedResults);
    calculateStandings(updatedResults);
  };

  const calculateStandings = (results) => {
    const standingsByPoule = {};

    results.forEach(({ teams, score1, score2 }) => {
      const [team1, team2] = teams.split(' vs ');
      const pouleIndex = schedule.find(match => match.teams === teams)?.poule;

      if (!standingsByPoule[pouleIndex]) standingsByPoule[pouleIndex] = {};
      const pouleStandings = standingsByPoule[pouleIndex];

      if (!pouleStandings[team1]) pouleStandings[team1] = { name: team1, played: 0, won: 0, points: 0, scored: 0, against: 0 };
      if (!pouleStandings[team2]) pouleStandings[team2] = { name: team2, played: 0, won: 0, points: 0, scored: 0, against: 0 };

      pouleStandings[team1].played += 1;
      pouleStandings[team2].played += 1;

      pouleStandings[team1].scored += score1;
      pouleStandings[team2].scored += score2;

      pouleStandings[team1].against += score2;
      pouleStandings[team2].against += score1;

      if (score1 > score2) {
        pouleStandings[team1].won += 1;
        pouleStandings[team1].points += 3;
      } else if (score2 > score1) {
        pouleStandings[team2].won += 1;
        pouleStandings[team2].points += 3;
      } else {
        pouleStandings[team1].points += 1;
        pouleStandings[team2].points += 1;
      }
    });

    for (let poule in standingsByPoule) {
      standingsByPoule[poule] = Object.values(standingsByPoule[poule]).sort((a, b) => {
        if (b.points !== a.points) return b.points - a.points;
        const scoreDifferenceA = a.scored - a.against;
        const scoreDifferenceB = b.scored - b.against;
        return scoreDifferenceB - scoreDifferenceA || a.against - b.against;
      });
    }

    setStandings(standingsByPoule);
  };

  return (
    <div>
      <h1>Volleyball Tournament Configuration</h1>
      {!config ? (
        <ConfigurationForm onSubmit={handleConfigSubmit} />
      ) : (
        <>
          <button onClick={generatePouleSchedule}>Generate Schedule</button>
          <PouleSchedule schedule={schedule} />
          <MatchResults schedule={schedule} onSaveResults={handleSaveResults} />
          <LiveStandings standings={standings} />
        </>
      )}
    </div>
  );
};

export default App;
