import React, { useState } from 'react';
import axios from 'axios';

const ConfigurationForm = () => {
    // State variables for form fields
    const [numTeams, setNumTeams] = useState(6);
    const [numPoules, setNumPoules] = useState(2);
    const [numCourts, setNumCourts] = useState(2);
    const [matchTime, setMatchTime] = useState(40);
    const [startTime, setStartTime] = useState("09:00");
    const [tournamentDuration, setTournamentDuration] = useState(8);
    const [message, setMessage] = useState("");
    const [isTournamentCreated, setIsTournamentCreated] = useState(false);

    // Function to handle creating the tournament
const handleCreateTournament = async () => {
    try {
        const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/tournament`, {
            numTeams,
            numPoules,
            numCourts,
            matchTime: parseInt(matchTime, 10),
            startTime,
            tournamentDuration,
        });

        if (response.status === 201) {
            setMessage("Toernooi succesvol aangemaakt!");
        }
    } catch (error) {
        setMessage("Er is een fout opgetreden bij het maken van het schema.");
        console.error('Error creating tournament:', error);
    }
 };

    // Function to handle generating the schedule
    const handleGenerateSchedule = async () => {
        try {
            const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/tournament/5/schedule`);
            if (response.status === 200) {
                setMessage("Schema succesvol gegenereerd!");
            }
        } catch (error) {
            setMessage("Er is een fout opgetreden bij het ophalen van het schema.");
            console.error('Error generating schedule:', error);
        }
    };

    return (
        <div>
            <h2>Configuratie</h2>
            <form onSubmit={(e) => e.preventDefault()}>
                <div>
                    <label>Aantal Teams:</label>
                    <input
                        type="number"
                        min="2"
                        value={numTeams}
                        onChange={(e) => setNumTeams(e.target.value)}
                    />
                </div>
                <div>
                    <label>Aantal Poules:</label>
                    <input
                        type="number"
                        min="1"
                        value={numPoules}
                        onChange={(e) => setNumPoules(e.target.value)}
                    />
                </div>
                <div>
                    <label>Aantal Velden:</label>
                    <input
                        type="number"
                        min="1"
                        value={numCourts}
                        onChange={(e) => setNumCourts(e.target.value)}
                    />
                </div>
                <div>
                    <label>Tijd per Wedstrijd (minuten):</label>
                    <input
                        type="number"
                        min="1"
                        value={matchTime}
	    		onChange={(e) => setMatchTime(parseInt(e.target.value, 10))}

                    />
                </div>
                <div>
                    <label>Start Tijd:</label>
                    <input
                        type="time"
                        value={startTime}
                        onChange={(e) => setStartTime(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Toernooi Duur (uren):</label>
                    <input
                        type="number"
                        min="1"
                        value={tournamentDuration}
                        onChange={(e) => setTournamentDuration(e.target.value)}
                    />
                </div>
                <button type="button" onClick={handleCreateTournament}>
                    Maak Toernooi
                </button>
            </form>
            {isTournamentCreated && (
                <button type="button" onClick={handleGenerateSchedule}>
                    Genereer Wedstrijdschema
                </button>
            )}
            {message && (
                <p className={message.includes('succesvol') ? 'success-message' : 'error-message'}>
                    {message}
                </p>
            )}
        </div>
    );
};

export default ConfigurationForm;
