// MatchResults.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/MatchResults.css';

function MatchResults({ tournamentId }) {
  const [matches, setMatches] = useState([]);
  const [scores, setScores] = useState({});

  // Fetch matches for the given tournament ID
  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/tournament/${tournamentId}/schedule`);
        if (response.status === 200) {
          setMatches(response.data);
        }
      } catch (error) {
        console.error('Error fetching matches:', error);
      }
    };

    if (tournamentId) {
      fetchMatches();
    }
  }, [tournamentId]);

  const handleChange = (matchId, team, value) => {
    setScores((prevScores) => ({
      ...prevScores,
      [matchId]: {
        ...prevScores[matchId],
        [team]: value,
      },
    }));
  };

  const handleSubmit = async (matchId) => {
    const scoresToSubmit = scores[matchId];
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/api/tournament/match/${matchId}/score`, scoresToSubmit);

      // Optionally, update the local state to reflect the saved scores
      setMatches((prevMatches) =>
        prevMatches.map((match) =>
          match.id === matchId ? { ...match, ...scoresToSubmit } : match
        )
      );
    } catch (error) {
      console.error('Error updating score:', error);
    }
  };

  return (
    <div>
      <h2>Wedstrijdresultaten</h2>
      {matches.map((match) => (
        <div key={match.id}>
          <p>{`Team ${match.team1_id} vs Team ${match.team2_id}`}</p>
          <input
            type="number"
            value={scores[match.id]?.team1 || ''}
            onChange={(e) => handleChange(match.id, 'team1', e.target.value)}
            placeholder={`Score Team ${match.team1_id}`}
          />
          -
          <input
            type="number"
            value={scores[match.id]?.team2 || ''}
            onChange={(e) => handleChange(match.id, 'team2', e.target.value)}
            placeholder={`Score Team ${match.team2_id}`}
          />
          <button onClick={() => handleSubmit(match.id)}>Submit Score</button>
        </div>
      ))}
    </div>
  );
}

export default MatchResults;
