// frontend/src/components/MatchResults.js
import React, { useState, useEffect } from 'react';
import '../styles/MatchResults.css';  // Import the CSS file

function MatchResults({ matches, onSaveResults }) {
  const [results, setResults] = useState([]);

  useEffect(() => {
    // Initialize results based on incoming matches structure
    setResults(matches.map(match => ({ ...match })));
  }, [matches]);

  const handleScoreChange = (index, field, value) => {
    const updatedResults = [...results];
    updatedResults[index][field] = parseInt(value, 10) || 0;
    setResults(updatedResults);
  };

  const handleSave = () => {
    onSaveResults(results);
  };

  return (
    <div className="match-results">
      <h2>Enter Match Results</h2>
      <table>
        <thead>
          <tr>
            <th>Match</th>
            <th>Score 1</th>
            <th>Score 2</th>
          </tr>
        </thead>
        <tbody>
          {results.map((match, index) => (
            <tr key={index}>
              <td>{match.teams}</td>
              <td>
                <input
                  type="number"
                  value={match.score1}
                  onChange={(e) => handleScoreChange(index, 'score1', e.target.value)}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={match.score2}
                  onChange={(e) => handleScoreChange(index, 'score2', e.target.value)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleSave}>Save Results</button>
    </div>
  );
}

export default MatchResults;
