// frontend/src/components/ConfigurationForm.js
import React, { useState, useEffect } from 'react';
import Button from './ui/button';
import Input from './ui/input';
import '../styles/ConfigurationForm.css';

function ConfigurationForm({ onSubmit }) {
  const [config, setConfig] = useState({
    numTeams: '',
    numPoules: '',
    numCourts: '',
    matchTime: '',
    startTime: '',
    tournamentDuration: '',
  });
  const [teamNames, setTeamNames] = useState([]);

  useEffect(() => {
    // Adjust the number of team names based on numTeams
    const teams = Array.from({ length: parseInt(config.numTeams) || 0 }, (_, i) => teamNames[i] || `Team ${i + 1}`);
    setTeamNames(teams);
  }, [config.numTeams]);

  const handleChange = (e) => {
    setConfig({
      ...config,
      [e.target.name]: e.target.value,
    });
  };

  const handleTeamNameChange = (index, e) => {
    const updatedTeamNames = [...teamNames];
    updatedTeamNames[index] = e.target.value;
    setTeamNames(updatedTeamNames);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ ...config, teamNames });
  };

  return (
    <form onSubmit={handleSubmit} className="configuration">
      <h2>Configuration</h2>
      <div className="form-group">
        <label>Number of Teams:</label>
        <Input
          name="numTeams"
          value={config.numTeams}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Number of Poules:</label>
        <Input
          name="numPoules"
          value={config.numPoules}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Number of Courts:</label>
        <Input
          name="numCourts"
          value={config.numCourts}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Match Time (mins):</label>
        <Input
          name="matchTime"
          value={config.matchTime}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Start Time:</label>
        <Input
          name="startTime"
          value={config.startTime}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Tournament Duration (mins):</label>
        <Input
          name="tournamentDuration"
          value={config.tournamentDuration}
          onChange={handleChange}
          required
        />
      </div>

      <h3>Team Names</h3>
      {teamNames.map((team, index) => (
        <div key={index} className="form-group">
          <label>Team {index + 1} Name:</label>
          <Input
            value={team}
            onChange={(e) => handleTeamNameChange(index, e)}
            required
          />
        </div>
      ))}

      <Button type="submit">Create Tournament</Button>
    </form>
  );
}

export default ConfigurationForm;
