// frontend/src/components/MatchResults.js
import React from 'react';
import '../styles/MatchResults.css';

function MatchResults({ matches, onSaveResults }) {
    return (
        <div>
            <h3>Enter Match Results</h3>
            <table>
                <thead>
                    <tr>
                        <th>Time</th>
                        <th>Match</th>
                        <th>Court</th>
                        <th>Referee</th>
                        <th>Score 1</th>
                        <th>Score 2</th>
                    </tr>
                </thead>
                <tbody>
                    {(matches || []).map((match, index) => (
                        <tr key={index}>
                            <td>{match.time}</td>
                            <td>{match.teams}</td>
                            <td>{match.court}</td>
                            <td>{match.referee}</td>
                            <td>
                                <input
                                    type="number"
                                    value={match.score1 || ''}
                                    onChange={(e) => onSaveResults(index, 'score1', e.target.value)}
                                />
                            </td>
                            <td>
                                <input
                                    type="number"
                                    value={match.score2 || ''}
                                    onChange={(e) => onSaveResults(index, 'score2', e.target.value)}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={() => onSaveResults()}>Save Results</button>
        </div>
    );
}

export default MatchResults;
